import 'package:path/path.dart';import 'package:sqflite/sqflite.dart';class Connection {  static final Connection _instance = Connection.getInstance();  static const DATABASE_NAME = 'simpleDataBase.db';  static const DATABASE_VERSION = 1;  static Database _db;  Connection.getInstance();  factory Connection() => _instance;  Future<Database> get db async {    if (_db != null) {      return _db;    }    _db = await _openConnectionDataBase();    return _db;  }  Future<Database> _openConnectionDataBase() async {    String dataBasePath = await getDatabasesPath();    String path = join(dataBasePath, DATABASE_NAME);    var database =        await openDatabase(path, version: DATABASE_VERSION, onUpgrade: _onUpgrade, onCreate: _onCreate);    return database;  }  Future<void> _onCreate(Database db, int version) async{    await db.execute('CREATE TABLE pets(id INTEGER PRIMARY KEY, name TEXT, status INTEGER)');  }  Future<void> _onUpgrade(Database db, int oldVersion, int newVersion) async{    await db.execute('CREATE TABLE people(id INTEGER PRIMARY KEY, name TEXT, status INTEGER)');  }}