import 'package:flutter/material.dart';import 'pet_controller.dart';import 'pet_model.dart';import 'pet_repository.dart';class PetDetailPage extends StatelessWidget {  final controller = PetController(PetRepository());  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text('Pet'),      ),      body: Container(        child: Padding(          padding: const EdgeInsets.all(8.0),          child: TextFormField(            decoration: InputDecoration(labelText: 'Name'),            controller: controller.textEditingController,          ),        ),      ),      floatingActionButton: FloatingActionButton(        onPressed: () async {          await controller              .save(PetModel(name: controller.textEditingController.text, status: false));          Navigator.of(context).pop();        },        child: Icon(Icons.check),      ), // This trailing comma makes    );  }}