class PetModel {  int id;  String name;  bool status;  PetModel({this.id, this.name, this.status});  PetModel.fromMap(Map<String, dynamic> map) {    this.id = map['id'];    this.name = map['name'];    this.status = map['status'] ==  0 ? false : true;  }  Map<String, dynamic> toMap() {    final Map<String, dynamic> data = new Map<String, dynamic>();    data['id'] = this.id;    data['name'] = this.name;    data['status'] = this.status;    return data;  }}